package javaPractice;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//1 to 10
		
		int i=0;
		
		while(i<=10)
		{
			System.out.println(i);
			i++;
		}
		
		//do while loop
		
		int j=10;
	
		do
		{
			System.out.println(j);
			j++;
		}
	while(j<20);
	
	}

}
